#include <stdio.h>
int main(int argc, char *argv[])
{
	char a[100];
	int num=0;
	int i=0;
	printf("请输入字符：");
	for(i = 0;i<35;i++)			/*这里i的值可以自己修改*/	
	{
		scanf("%c",&a[i]);	
		num++;
	} 
	for(i=0;i<num;i++)
	{
		if(a[i]!=' ')
		a[i]-=1;
	}
	for(i=0;i<num;i++)
	{
		printf("%c",a[i]);
	}
	printf("\n");
	return 0;
}

